    { name: 'Ê≥ï‰∫∫„Å´„Å§„ÅÑ„Å¶', path: '/about' },
    { name: '„ÅäÁü•„Çâ„Åõ', path: '/news' },
    { name: '„ÅäÂïè„ÅÑÂêà„Çè„Åõ', path: '/contact' }
  ];

  const isActive = (path) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  return (
    <header className="header">
      <div className="container">
